import React, { useEffect, useState } from "react";
import axios from "axios";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

function ReportsPage() {
  const [report, setReport] = useState({});

  useEffect(() => {
    const fetchReport = async () => {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/expenses/report", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReport(res.data);
    };
    fetchReport();
  }, []);

  const pieData = {
    labels: report.categories ? Object.keys(report.categories) : [],
    datasets: [
      {
        data: report.categories ? Object.values(report.categories) : [],
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  };

  const lineData = {
    labels: report.trend ? Object.keys(report.trend) : [],
    datasets: [
      {
        label: "Daily Spend",
        data: report.trend ? Object.values(report.trend) : [],
        fill: false,
        borderColor: "blue",
      },
    ],
  };

  return (
    <div>
      <h2>Reports</h2>
      <Pie data={pieData} />
      <Line data={lineData} />
    </div>
  );
}

export default ReportsPage;
