import React, { useEffect, useState } from "react";
import axios from "axios";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
} from "chart.js";

ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

function ReportsPage() {
  const [report, setReport] = useState({});

  useEffect(() => {
    const fetchReport = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          "http://localhost:5000/api/expenses/report",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setReport(res.data);
      } catch (err) {
        alert("Failed to fetch report");
      }
    };
    fetchReport();
  }, []);

  const pieData = {
    labels: report.categories ? Object.keys(report.categories) : [],
    datasets: [
      {
        label: "Spending by Category",
        data: report.categories ? Object.values(report.categories) : [],
        backgroundColor: [
          "#FF6384",
          "#36A2EB",
          "#FFCE56",
          "#81C784",
          "#BA68C8",
        ],
      },
    ],
  };

  const lineData = {
    labels: report.trend ? Object.keys(report.trend) : [],
    datasets: [
      {
        label: "Daily Spending",
        data: report.trend ? Object.values(report.trend) : [],
        fill: false,
        borderColor: "#42A5F5",
      },
    ],
  };

  return (
    <div>
      <h2>Reports</h2>
      <Pie data={pieData} />
      <Line data={lineData} />
    </div>
  );
}

export default ReportsPage;
