import React, { useEffect, useState } from "react";
import axios from "axios";
import ExpenseForm from "./ExpenseForm";

function ExpensesList() {
  const [expenses, setExpenses] = useState([]);
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const [editingExpense, setEditingExpense] = useState(null);

  // Filter state
  const [filters, setFilters] = useState({
    date: "",
    category: "",
    paymentMethod: "",
  });

  const fetchExpenses = async () => {
    const token = localStorage.getItem("token");
    const res = await axios.get("http://localhost:5000/api/expenses", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setExpenses(res.data);
    setFilteredExpenses(res.data);
    setEditingExpense(null);
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  // Update filters and apply them
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    const updatedFilters = { ...filters, [name]: value };
    setFilters(updatedFilters);

    const filtered = expenses.filter((exp) => {
      const matchesDate = updatedFilters.date
        ? exp.date === updatedFilters.date
        : true;
      const matchesCategory = updatedFilters.category
        ? exp.category
            .toLowerCase()
            .includes(updatedFilters.category.toLowerCase())
        : true;
      const matchesPayment = updatedFilters.paymentMethod
        ? exp.paymentMethod
            .toLowerCase()
            .includes(updatedFilters.paymentMethod.toLowerCase())
        : true;

      return matchesDate && matchesCategory && matchesPayment;
    });

    setFilteredExpenses(filtered);
  };

  const deleteExpense = async (id) => {
    const token = localStorage.getItem("token");
    if (window.confirm("Are you sure you want to delete this expense?")) {
      await axios.delete(`http://localhost:5000/api/expenses/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchExpenses();
    }
  };

  return (
    <div className="container">
      <h2>All Expenses</h2>

      {/* Filters */}
      <div style={{ marginBottom: "20px" }}>
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
          placeholder="Filter by Date"
        />
        <input
          type="text"
          name="category"
          value={filters.category}
          onChange={handleFilterChange}
          placeholder="Filter by Category"
          style={{ marginLeft: "10px" }}
        />
        <input
          type="text"
          name="paymentMethod"
          value={filters.paymentMethod}
          onChange={handleFilterChange}
          placeholder="Filter by Payment Method"
          style={{ marginLeft: "10px" }}
        />
      </div>

      {editingExpense && (
        <ExpenseForm expenseToEdit={editingExpense} onSuccess={fetchExpenses} />
      )}

      <ul>
        {filteredExpenses.length === 0 ? (
          <li>No matching expenses found.</li>
        ) : (
          filteredExpenses.map((exp) => (
            <li key={exp._id}>
              â‚¹{exp.amount} - {exp.category} on {exp.date} ({exp.paymentMethod})
              <button onClick={() => setEditingExpense(exp)}>Edit</button>
              <button onClick={() => deleteExpense(exp._id)}>Delete</button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default ExpensesList;
